script:
(function() {
setInterval(function() {
try {
let _n = new Date(), _d = document.getElementById('main-header-daily-message-date'), _t = document.getElementById('main-header-daily-message-time');
_d.innerHTML = Time.DateAsString(_n);
_t.innerHTML = Time.TimeAsString(_n);
} catch (_e) { console.error(_e); }
}, 1000);
})();

(function() {
setInterval(function() {
try {
document.getElementById('main-header-daily-message-elapsed-time').innerHTML = Time.TimeDifferenceAsString(new Date('2015-09-30T17:16:41'), new Date(), false);
} catch (_e) { console.error(_e); }
}, 1000);
})();
